settings.custom_liquid:
{% assign collection_handle = 'all' %}
{% assign collection = collections[collection_handle] %}
{% assign prev_product = nil %}
{% assign next_product = nil %}

{% for item in collection.products %}
  {% if next_product == nil and item.handle == product.handle %}
    {% assign next_product = forloop.index0 | plus: 1 %}
  {% endif %}
  {% if item.handle == product.handle %}
    {% assign prev_product = forloop.index0 | minus: 1 %}
  {% endif %}
{% endfor %}

{% if prev_product != nil %}
  {% assign prev_product = collection.products[prev_product] %}
{% endif %}
{% if next_product != nil %}
  {% assign next_product = collection.products[next_product] %}
{% endif %}

<script>
  document.addEventListener("DOMContentLoaded", function () {
    var prev_product = {{ prev_product | json }};
    var next_product = {{ next_product | json }};
    var buttonsHtml = '';

    if (prev_product) {
      buttonsHtml += '<a href="' + prev_product.url + '" class="previous-product">&#8592; ' + prev_product.title + '</a>';
    }

    if (next_product) {
      buttonsHtml += '<a href="' + next_product.url + '" class="next-product">' + next_product.title + ' &#8594;</a>';
    }

    var targetElement = document.querySelector(".product-single"); // Change this selector to target the desired position
    var buttonsContainer = document.createElement("div");
    buttonsContainer.className = "prev-next-buttons";
    buttonsContainer.innerHTML = buttonsHtml;
    targetElement.parentNode.insertBefore(buttonsContainer, targetElement.nextSibling); // Change 'nextSibling' to 'previousSibling' to insert the buttons before the target element
  });
</script>
